changeSet.sql:
CREATE SEQUENCE ip_address_seq;
CREATE SEQUENCE network_interface_seq;
CREATE SEQUENCE vlan_seq;
CREATE SEQUENCE device_seq;
CREATE SEQUENCE test_script_seq;
CREATE SEQUENCE network_seq;
CREATE SEQUENCE network_service_seq;

CREATE TABLE device(
	id INT NOT NULL PRIMARY KEY,
	name VARCHAR(100) NOT NULL,
	description VARCHAR(255) NOT NULL
);

CREATE TABLE vlan(
	id INT NOT NULL PRIMARY KEY,
	name VARCHAR(255) NOT NULL,
	number INT NOT NULL,
	type VARCHAR(20) NOT NULL CHECK(type IN('QinQ','VXLAN'))
);

CREATE TABLE network_interface(
	id INT NOT NULL PRIMARY KEY,
	name VARCHAR(100) NOT NULL,
	description VARCHAR(255) NOT NULL,
	type VARCHAR(50) NOT NULL CHECK(type IN('TRUNK','ACCESS'))
);

CREATE TABLE parameter(
	id INT NOT NULL PRIMARY KEY,
	test_script INT NOT NULL REFERENCES test_script(id),
	value VARCHAR(255) NOT NULL
);

CREATE TABLE ip_address(
	id INT NOT NULL PRIMARY KEY,
	address VARCHAR(100) NOT NULL,
	type VARCHAR(10) NOT NULL CHECK(type IN('IPv4','IPv6')),
	network_interface_id INT REFERENCES network_interface(id)
);

CREATE TABLE vlan_vlan(
	first_vlan_id INT NOT NULL REFERENCES vlan(id),
	second_vlan_id INT NOT NULL REFERENCES vlan(id)
);

CREATE TABLE device_device(
	first_device_id INT NOT NULL REFERENCES device(id),
	second_device_id INT NOT NULL REFERENCES device(id)
);

CREATE TABLE network_interface_vlan(
	network_interface_id INT NOT NULL REFERENCES network_interface(id),
	vlan_id INT NOT NULL REFERENCES vlan(id)
);

CREATE TABLE vlan_network(
	network_id INT NOT NULL REFERENCES network(id),
	vlan_id INT NOT NULL REFERENCES vlan(id)
);

CREATE TABLE test_script_vlan(
	test_script_id INT NOT NULL REFERENCES test_script(id),
	vlan_id INT NOT NULL REFERENCES vlan(id)
);

CREATE TABLE test_script_device(
	test_script_id INT NOT NULL REFERENCES test_script(id),
	device_id INT NOT NULL REFERENCES device(id)
);

CREATE TABLE test_script_network_service(
	test_script_id INT NOT NULL REFERENCES test_script(id),
	network_service_id INT NOT NULL REFERENCES network_service(id)
);

CREATE TABLE network_interface_device(
	network_interface_id INT NOT NULL REFERENCES network_interface(id),
	device_id INT NOT NULL REFERENCES device(id)
);
